import axios from 'axios'
import React, { useState } from 'react'

const AxiosPost = () => {
  const [data, setData] = useState({
    title: '',
    body: ''
  })

  const handleChange = (e) => {
    const {name, value} = e.target
    setData({...data, [name]: value})
  }

  const handleSave = () => {
    axios.post('')
  }


  return (
    <div>
      <h2>POST API using axios</h2>
      <textarea placeholder='Title' value={data.title} onChange={handleChange}/>
      <br/><br/>
      <textarea placeholder='Body' value={data.body} onChange={handleChange}/>
      <br/><br/>
      <button>Save</button>
    </div>
  )
}

export default AxiosPost
