import React, { useEffect, useState } from 'react'
// import './StorageTask.css'

const StorageTask = () => {
  const [name, setName] = useState('')
  const [email, setEmail] = useState('')

  const handleChangeName = (e) => {
    setName(e.target.value)
  }

  const handleChangeEmail = (e) => {
    setEmail(e.target.value)
  }

  useEffect(() => {
    const localName = localStorage.getItem('name')
    if (localName) {
      setName(localName)
    } 
    const localEmail = localStorage.getItem('email')
    if (localEmail) {
      setEmail(localEmail)
    }
  })


  return (
    <form>
      <div>
        <h2>Storage Form</h2>
        <input type='text' placeholder='Name' value={name} onChange={handleChangeName}/>
        <input type='email' placeholder='Email' value={email} onChange={handleChangeEmail}/>
        <br/><br/>
        <button>Save to Local</button>
        <button>Save to Session</button>
        <br/><br/>
        <button>Clear Local</button>
        <button>Clear Session</button>

      </div>
    </form>
  )
}

export default StorageTask
