import React, {useRef} from "react";

const UseRefHook = () => {
    let inputRef = useRef(null)
    return (
        <div>
            <h1>UseRef Hook</h1>
            <input type='text' ref={inputRef}/>
            <></>
        </div>
    )
}

export default UseRefHook
