import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaSearch, FaEdit, FaTrash, FaArrowLeft, FaArrowRight } from 'react-icons/fa'; 
import './UserList.css';

const UserList = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const navigate = useNavigate();
  const [users, setUsers] = useState(JSON.parse(localStorage.getItem('users')) || []);

  const [usersPerPage, setUsersPerPage] = useState(5); 
  const [currentPage, setCurrentPage] = useState(1);

  const filteredUsers = users.filter(user =>
    user.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    user.email.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const indexOfLastUser = currentPage * usersPerPage;
  const indexOfFirstUser = indexOfLastUser - usersPerPage;
  const currentUsers = filteredUsers.slice(indexOfFirstUser, indexOfLastUser);
  const totalPages = Math.ceil(filteredUsers.length / usersPerPage);

  const pageButtonLimit = 3;
  const getPaginationRange = () => {
    const startPage = Math.max(1, currentPage - Math.floor(pageButtonLimit / 2));
    const endPage = Math.min(totalPages, startPage + pageButtonLimit - 1);
    return { startPage, endPage };
  };

  const { startPage, endPage } = getPaginationRange();

  const handleEdit = (user) => {
    navigate('/edit-user', { state: { user } });
  };

  const handleDelete = (userIndexInPage) => {
    const confirmDelete = window.confirm("Are you sure you want to delete this user?");

    if (confirmDelete) {
      const userIndexInFullList = indexOfFirstUser + userIndexInPage;
      const updatedUsers = users.filter((_, i) => i !== userIndexInFullList);
      localStorage.setItem('users', JSON.stringify(updatedUsers));
      setUsers(updatedUsers);

      if (currentUsers.length === 1 && currentPage > 1) {
        setCurrentPage(currentPage - 1);
      }
    }
  };

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    }
  };

  const handlePreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const handleUsersPerPageChange = (e) => {
    setUsersPerPage(Number(e.target.value));
    setCurrentPage(1); 
  };

  const isDataSubmitted = filteredUsers.length < usersPerPage && filteredUsers.length > 0;

  return (
    <div>
      <h2>User List</h2>

      <div className="search-container"> 

      <input type="search"  placeholder= "Search by name or email..." value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)} />
        <FaSearch />
      </div>

      {currentUsers.length === 0 ? (
        <p>No User Found.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Date of Birth</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {currentUsers.map((user, index) => (
              <tr key={index}>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>{user.date_of_birth}</td>
                <td>
                  <span onClick={() => handleEdit(user)} title="Edit User">
                    <FaEdit />
                  </span>
                  <span onClick={() => handleDelete(index)} title="Delete User"
                    style={{ marginLeft: '10px', color: 'red' }}>
                    <FaTrash />
                  </span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}


      <div className="pagination-controls">
        <span 
          className={currentPage === 1 ? 'disabled' : ''} 
          onClick={handlePreviousPage}
          style={{ cursor: currentPage > 1 ? 'pointer' : 'not-allowed' }} >
          <FaArrowLeft />
        </span>

        {Array.from({ length: endPage - startPage + 1 }, (_, i) => (
          <button 
            key={i + startPage} 
            onClick={() => handlePageChange(i + startPage)}
            className={currentPage === i + startPage ? 'active-page' : ''}

            style={{ fontWeight: currentPage === i + startPage ? 'bold' : 'normal',
              backgroundColor: currentPage === i + startPage ? 'black' : '#fff',
              color: currentPage === i + startPage ? '#fff' : 'black' }} >

            {i + startPage}
          </button>
        ))}

        <span className={currentPage === totalPages ? 'disabled' : ''} 
          onClick={handleNextPage}
          style={{ cursor: currentPage < totalPages ? 'pointer' : 'not-allowed' }} >
          <FaArrowRight />
        </span>

        <select value={usersPerPage} onChange={handleUsersPerPageChange} 
        style={{ width: '45px', marginLeft: '10px', cursor: 'pointer' }}>

          <option value={5}>5</option>
          <option value={10}>10</option>
          <option value={20}>20</option>
          <option value={50}>50</option>
          <option value={100}>100</option>

        </select>
      </div>
    </div>
  );
};

export default UserList;
