import React, { useEffect, useState } from 'react'

const HookTask2 = () => {
  const [search, setSearch] = useState('')
  const [filter, setFilter] = useState([])

  const users = [
    {id: 1, name: 'John'},
    {id: 2, name: 'Abc'},
    {id: 3, name: 'Peter'},
    {id: 4, name: 'Bruce'},
    {id: 5, name: 'xyz'}
  ]

  const handleSearch = (e) => {
    setSearch(e.target.value)
  }

  useEffect(() => {
    const filterUser = users.filter(users => 
      users.toLowerCase().includes(search.toLowerCase())
    )
    setFilter(filterUser)
  }, [search])

  // const filterUser = users.filter((users) => 
  //   users.name.toLowerCase().includes(search.toLowerCase())
  // ) 

  return (
    <div>
      <h2>Filter List</h2>
      <input type='text' placeholder='search user' value={search} onChange={handleSearch}/>

      <ul>
        {}
      </ul>
    </div>
  )
}

export default HookTask2
