import React, {useState, useEffect} from 'react'

const EffectCountdown = () => {
    const maxTime = 1 * 60
    const [time, setTime] = useState(0)

    useEffect(() => {
        if (time >= maxTime) {
            return
        }

        setTimeout(() => {
            console.log('setTimeout')
            setTime(time + 1)
        }, 1000);
    }, [maxTime])

  return (
    <div>
        <h2>Countdown</h2>
        <h3>{time}</h3>
    </div>
  )
}

export default EffectCountdown
