import React, {useState, useEffect} from 'react'

const UseEffectHook = (start) => {
    const [time, setTime] = useState(start)

    useEffect(() => {
        if (time <= 0) {
            return
        }

        setTimeout(() => {
            setTime(time - 1)
        }, 1000);
    }, [time])

  return (
    <div>
        <h2>Countdown</h2>
        <h3>{time}</h3>
    </div>
  )
}

export default UseEffectHook
