  //       <input type="number" value={input} onChange={(e) => setInput(e.target.value)} />
  //     </div>
  //   </div>
  // )

  return (
    <div>
      <h2>Count: {count}</h2>
      <button onClick={() => setCount(count + 1)}>Increment</button>

      <div>
        <h2>Result: {value}</h2>
        <input type=''/>
      </div>
    </div>
  )
}

export default UseMemoHook;
