import React, { useEffect, useState } from 'react'

const StorageTask1 = () => {
    const [user, setUser] = useState([])

    useEffect(() => {
        const storeUser = JSON.parse(localStorage.getItem('user') || [])
        setUser(storeUser)
    }, [])

    const array = [
        {id: 1, name: 'Alice', role: 'user'},
        {id: 2, name: 'John', role: 'admin'},
        {id: 3, name: Charlie, role: 'user'}
    ]

    const saveLocal = () => {
        localStorage.setItem('user', JSON.stringify(array))
        setUser(array)
    }

    const saveSession = () => {
        sessionStorage.setItem('user', JSON.stringify(array))
        setUser(array)
    }

    const retrieveLocal = () => {
        const storeUser = JSON.parse(localStorage.getItem('user') || [])
        setUser(storeUser)
    }

    const retrieveSession = () => {
        const storeUser = JSON.parse(sessionStorage.getItem('user') || [])
        setUser(storeUser)
    }

    const clearLocal = () => {
        localStorage.removeItem('user')
        setUser([])
    }

    const clearSession = () => {
        sessionStorage.removeItem('user')
        setUser([])
    }

  return (
    <div>
      <h2>Store Array and Object</h2>
      <button onClick={saveLocal}>Save to Local</button>
      <button onClick={saveSession}>Save to Session</button>
      <button onClick={retrieveLocal}>Retrieve to Local</button>
      <button onClick={retrieveSession}>Retrieve to Session</button>
      <button onClick={clearLocal}>Clear to Local</button>
      <button onClick={clearSession}>Clear to Session</button>
    </div>
  )
}

export default StorageTask1
