import React, { useState, useEffect} from 'react';

const users = [
  { id: 1, name: "John" },
  { id: 2, name: "Abc" },
  { id: 3, name: "Peter" },
  { id: 4, name: "Bruce" },
  { id: 5, name: "xyz" },
];

function HookTask2() {
	const [data, setData] = useState('');
	const [filteredItems, setFilteredItems] = useState([]);

	useEffect(() => {
		const filtered = users.filter(users =>
			users.name.toLowerCase().includes(data.toLowerCase())
		);
		setFilteredItems(filtered);
	}, [data]);

	return (
		<div>
      <h2>Filter List</h2>
			<input
				type="text"
				placeholder="Search..."
				value={data}
				onChange={(e) => setData(e.target.value)}
			/>
			<ul>
				{filteredItems.map((users) => (
					<li key={users.id}>{users.name}</li>
				))}
			</ul>
		</div>
	);
}

export default HookTask2;
