import React, {useState} from "react";

const UserState = () => {
    const [count, setCount] = useState(0)

    const incFunction = () => {
        setCount(count + 1)
    }

    const decFunction = () => {
        setCount(count - 1)
    }

    return (
        <div>
            <h1>{count}</h1>
            <button onClick={() => {incFunction()}}>Increment</button>
            <button>Decrement</button>
        </div>
    )
}

export default UserState
