import React, { useMemo } from "react";

const HookTask1 = () => {
  const email = "abc@gmail.com";

  const formattedEmail = useMemo(() => {
    if (!email) {
      return "";
    }

    const [name, domain] = email.split("@");
    console.log(name);
    log
    if (!domain) {
      return email;
    }

    return `${name.slice(0, 2)}**@${domain}`;
  }, [email]); 

  return (
    <div>
      <h2>Email</h2>
      <p>{formattedEmail}</p>
    </div>
  );
};

export default HookTask1;
