import React, { Component } from 'react'

class User extends Component
{
    constructor()
    {
        super()
        this.state = {
            count: 0
        }
    }


    render() 
    {
        return(
            <div>
                <h1>React</h1>
                <p>{this.state.name}</p>
                <button onClick={() => {this.setState({count: this.state.count + 1})}}>Update</button>
            </div>
        )
    }
}

export default User
