import React, { useState } from 'react';
import { FaEye, FaEyeSlash } from 'react-icons/fa'; 
import { useNavigate } from 'react-router-dom'; 
import './FormWithValidation.css'; 

function FormWithValidation() {
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [formValues, setFormValues] = useState({
    name: '',
    email: '',
    date_of_birth: '',
    password: '',
    confirm_password: '',
  });
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();  

  const togglePasswordVisibility = () => setShowPassword(!showPassword);
  const toggleConfirmPasswordVisibility = () => setShowConfirmPassword(!showConfirmPassword);

  const inputStyle = {
    width: '100%',
    paddingRight: '40px',
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormValues({ ...formValues, [name]: value });
  };

  const isAtLeast18 = (dateOfBirth) => {
    const today = new Date();
    const dob = new Date(dateOfBirth);
    let age = today.getFullYear() - dob.getFullYear();
    const monthDifference = today.getMonth() - dob.getMonth();

    if (monthDifference < 0 || (monthDifference === 0 && today.getDate() < dob.getDate())) {
      age--;
    }
    return age >= 18;
  };

  const validate = () => {
    let newErrors = {};
    if (!formValues.name) {
      newErrors.name = 'Name is required';
    } else if (formValues.name.length < 3) {
      newErrors.name = 'Name must be at least 3 characters long';
    }
    if (!formValues.email) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formValues.email)) {
      newErrors.email = 'Email is invalid';
    }
    if (!formValues.date_of_birth) {
      newErrors.date_of_birth = 'Date of Birth is required';
    } else if (!isAtLeast18(formValues.date_of_birth)) {
      newErrors.date_of_birth = 'You must be at least 18 years old';
    }
    if (!formValues.password) {
      newErrors.password = 'Password is required';
    } else if (formValues.password.length < 6) {
      newErrors.password = 'Password must be at least 6 characters long';
    }
    if (!formValues.confirm_password) {
      newErrors.confirm_password = 'Confirm Password is required';
    } else if (formValues.password !== formValues.confirm_password) {
      newErrors.confirm_password = 'Passwords do not match';
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (validate()) {
      alert('Data Submitted Successfully!!');

      const existingUsers = JSON.parse(localStorage.getItem('users')) || [];
      existingUsers.push(formValues);
      localStorage.setItem('users', JSON.stringify(existingUsers));

      try {
        const response = await fetch('https://jsonplaceholder.typicode.com/users', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            name: formValues.name,
            email: formValues.email,
            date_of_birth: formValues.date_of_birth,
            password: formValues.password,
          }),
        });

        if (!response.ok) {
          throw new Error( 'Network response was not ok');
        }

        const data = await response.json();
        console.log('API Response:', data);

      } catch (error) {
        console.error('Error during API POST:', error);

        alert('Failed to submit data to the server.');
      }

      setFormValues({
        name: '',
        email: '',
        date_of_birth: '',
        password: '',
        confirm_password: '',
      });
      setErrors({});
    }
  };

  return (
    <div className="form-container">
      <h2>Registration Form</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name: </label>
          <input type="text" name="name" value={formValues.name} onChange={handleChange} />
          {errors.name && <p style={{ color: 'red' }}>{errors.name}</p>}
        </div>
        <div>
          <label>Email: </label>
          <input type="email" name="email" value={formValues.email} onChange={handleChange} />
          {errors.email && <p style={{ color: 'red' }}>{errors.email}</p>}
        </div>
        <div>
          <label>Date of Birth: </label>
          <input type="date" name="date_of_birth" value={formValues.date_of_birth} onChange={handleChange} />
          {errors.date_of_birth && <p style={{ color: 'red' }}>{errors.date_of_birth}</p>}
        </div>
        <div>
          <label>Password: </label>
          <div style={{ position: 'relative', width: '300px' }}>
            <input type={showPassword ? 'text' : 'password'} name="password"
              value={formValues.password} onChange={handleChange} style={inputStyle} />
            <span onClick={togglePasswordVisibility}
              style={{
                position: 'absolute',
                left: '295px',
                top: '45%',
                transform: 'translateY(-50%)',
                cursor: 'pointer',
                zIndex: 1,
                color: '#666',
              }} >
              {showPassword ? <FaEyeSlash /> : <FaEye />}
            </span>
          </div>
          {errors.password && <p style={{ color: 'red' }}>{errors.password}</p>}
        </div>
        <div>
          <label>Confirm Password: </label>
          <div style={{ position: 'relative', width: '300px' }}>
            <input type={showConfirmPassword ? 'text' : 'password'} name="confirm_password"
              value={formValues.confirm_password} onChange={handleChange} style={inputStyle} />
            <span onClick={toggleConfirmPasswordVisibility}
              style={{
                position: 'absolute',
                left: '295px',
                top: '45%',
                transform: 'translateY(-50%)',
                cursor: 'pointer',
                zIndex: 1,
                color: '#666',
              }} >
              {showConfirmPassword ? <FaEyeSlash /> : <FaEye />}
            </span>
          </div>
          {errors.confirm_password && <p style={{ color: 'red' }}>{errors.confirm_password}</p>}
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}


export default FormWithValidation;
