import React, {useState, useEffect} from 'react'

const UseEffectHook = () => {
    const [time, setTime] = useState(10)

    useEffect(() => {
        if(!time)
        return

        const timing = setInterval(() => {
            setTime(time - 1)
        }, 1000);

        return () =>clearInterval(timing)
    }, [time])

  return (
    <div>
        <h2>Countdown</h2>
        <></>
    </div>
  )
}

export default UseEffectHook
