const LocalStorage = () => {
    const [notes, setNotes] = useState('')

    useEffect(() => {
        const savedNotes = localStorage.getItem
    })

  return (
    <div>
      <h2>Notes</h2>
      <textarea value={notes} onChange={(e) => setNotes(e.target.value)}/>
    </div>
  )
}

