import React, { useState } from 'react'

const users = [
  {id: 1, name: 'John'},
  {id: 2, name: 'Abc'},
  {id: 3, name: 'Peter'},
  {id: 4, name: 'Bruce'},
  {id: 5, name: 'xyz'}
]

const HookTask2 = () => {
  const [search, setSearch] = useState('')

  const handleSearch = (e) => {
    setSearch(e.target.value)
  }

  const filterUser = users.filter((users) => 
    users.name.toLowerCase().includes(search.toLowerCase())
  )

  const userList = filterUser.map((users) => <p>{users.name}</p>)
  if(!filterUser) {
    return 'No user '
  }

  return (
    <div>
      <h2>Filter List</h2>
      <input type='text' placeholder='search user' value={search} onChange={handleSearch}/>

      <div>
        <p>{userList}</p>
      </div>
    </div>
  )
}

export default HookTask2
