import React, { useEffect, useState } from 'react';
import axios from 'axios';

const PutApiAxios = () => {
    const [data, setData] = useState([]);
    const [formData, setFormData] = useState({
        id: null,
        title: '',
        body: ''
    });

    const fetchData = () => {
        axios.get('https://jsonplaceholder.typicode.com/posts')
            .then((resp) => {
                console.log('Fetched Data:', resp.data);
                setData(resp.data);
                setFormData({
                    id: resp.data[0].id,
                    title: resp.data[0].title,
                    body: resp.data[0].body
                });
            })
            .catch((error) => console.error('Error fetching data:', error));
    };

    useEffect(() => {
        fetchData();
    }, []); 

    const editData = (id) => {
        const userData = data.find((item) => item.id === id);
        if (userData) {
            setFormData({
                id: userData.id,
                title: userData.title,
                body: userData.body
            });
        }
    };

    const handleUpdate = () => {
        if (!formData.id) return;

        axios.put(`https://jsonplaceholder.typicode.com/posts/${formData.id}`, formData, {
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
        })
        .then((resp) => {
            console.log('Updated Data:', resp.data);
        })
        .catch((error) => console.error('Error updating data:', error));
    };

    return (
        <div>
            <h2>PUT API Method using axios</h2>
            <div>
                <textarea
                    placeholder='Title'
                    value={formData.title}
                    onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                />
                <br /><br />
                <textarea
                    placeholder='Body'
                    value={formData.body}
                    onChange={(e) => setFormData({ ...formData, body: e.target.value })}
                />
                <br /><br />
                <button onClick={handleUpdate}>Update</button>
            </div>
            <ul>
                {data.map((item) => (
                    <li key={item.id}>
                        <h4>User ID: {item.id}</h4>
                        <h5>Title: {item.title}</h5>
                        <p>Body: {item.body}</p>
                        <button onClick={() => editData(item.id)}>Edit</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default PutApiAxios;
