import React, {useState} from 'react'

const UserState = () => {
    const [count, setCount] = useState(0)



    const addValue = () => {
        if (count < 20) {
            setCount(count + 1)
        }
    }

    const removeValue = () => {
        if (count > 0 ) {
            setCount(count - 1)
        }
    }

    return (
        <div>
            <h1>react</h1>
            <p>{count}</p>
            <button onClick={() => {addValue()}}>Add</button>
            <button onClick={() => {removeValue()}}>Remove</button>
        </div>
    )
}

export default UserState
