import React, { useState, useMemo } from "react";

const EmailFormatter = () => {
  const [email, setEmail] = useState("ab**@gmail.com");

  const formattedEmail = useMemo(() => {
    const [name, domain] = email.split("@");
    if (name.length > 2) {
      return name.slice(0, 2) + "**" + "@" + domain;
    }
    return email;
  }, [email]);

  return (
    <div className="p-4 border rounded-lg shadow-md max-w-md mx-auto mt-10">
      <h2 className="text-xl font-bold mb-2">Formatted Email</h2>
      <p className="text-gray-700">Your email is displayed as: <strong>{formattedEmail}</strong></p>
      <p className="text-sm text-gray-500 mt-2">
        The email format ensures privacy by masking part of the address.
      </p>
    </div>
  );
};

export default EmailFormatter;
