import React, {useState} from "react";

function UserState() 
{
    const [count, setCount] = useState(0)
    console.log(count)

    const incFunction = () => {
        setCount(count + 1)
    }

    const decFunction = () => {
        setCount(count - 1)
    }

    return(
        <div>
            <h1>React</h1>
            <p>{count}</p>
            <button onClick={incFunction}>Increase</button>
            <button onClick={decFunction}>Decrease</button>
        </div>
    )
}

export default UserState
