import { useState, useEffect } from "react";

export default function CountdownTimer() {
  const initialTime = 10 * 60; // 10 minutes in seconds
  const [timeLeft, setTimeLeft] = useState(initialTime);

  useEffect(() => {
    if (timeLeft <= 0) return; // Stop countdown at 0

    const timer = setInterval(() => {
      setTimeLeft((prev) => prev - 1);
    }, 1000);

    return () => clearInterval(timer); // Cleanup on unmount
  }, [timeLeft]);

  // Convert seconds to MM:SS format
  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${minutes}:${secs < 10 ? "0" : ""}${secs}`;
  };

  return (
    <div className="p-4 text-center">
      <h2 className="text-xl font-bold mb-2">Countdown Timer</h2>
      <p className="text-2xl font-semibold text-red-600">{formatTime(timeLeft)}</p>
    </div>
  );
}
