import React, { useState, useEffect } from "react";

const CountdownTimer = () => {
  const maxTime = 10 * 60; // 10 minutes in seconds
  const [time, setTime] = useState(0); // Start from 0 seconds

  useEffect(() => {
    if (time >= maxTime) return; // Stop when reaching maxTime

    const timer = setInterval(() => {
      setTime((prevTime) => prevTime + 1); // Increase time by 1 sec
    }, 1000);

    return () => clearInterval(timer); // Cleanup interval on re-render/unmount
  }, [time]);

  // Convert seconds to MM:SS format
  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${minutes}:${secs < 10 ? "0" : ""}${secs}`;
  };

  return (
    <div style={{ textAlign: "center", fontSize: "20px", marginTop: "20px" }}>
      <h2>Timer Counting Up</h2>
      <h3>{formatTime(time)}</h3>
    </div>
  );
};

export default CountdownTimer;
