import React, {useState} from 'react'

const UserState = () => {
    const [data, setData] = useState('abc')

    const updateData = () => {
        setData('xyz')
    }

    return(
        <div>
            <h1>{data}</h1>
            <button onClick={() => {updateData()}}>Update</button>
        </div>
    )
}

export default UserState
