import React from 'react'

const emailFormate = (email) => {
  if (!email) return "";

  const [name, domain] = email.split("@");
  if (!domain) return email;

  return `${name.slice(0, 2)}**@${domain}`;

}

const HookTask1 = () => {
  return (
    <div>
      <h2>Email</h2>
      <p>{emailFormate('abc@gmail.com')}</p>
    </div>
  )
}

export default HookTask1
