import React, { useState, useEffect } from 'react';

const UseEffectHook = () => {
    const [time, setTime] = useState(10 * 60); // 10 minutes in seconds

    useEffect(() => {
        if (time <= 0) return;

        const timeout = setTimeout(() => {
            console.log('setTimeout triggered');
            setTime(prevTime => prevTime - 1); // ✅ Use functional update
        }, 1000);

        return () => clearTimeout(timeout); // ✅ Cleanup timeout to prevent issues
    }, [time]); 

    return (
        <div>
            <h2>Countdown</h2>
            <h3>{time}</h3>
        </div>
    );
};

export default UseEffectHook;
